mapper.select:

select comment_id, post_id, member_id, member_name, comment_date, comment_content
	from
	(
		select /*+INDEX(comments comment_id_pk)*/
			rownum rn, comment_id, post_id, member_id, member_name, comment_date, comment_content
			from comments
			where post_id = #{post_id} and comment_id > 0
			and rownum <= #{cri.pageNum} * #{cri.amount}
	) where rn > (#{cri.pageNum} - 1) * #{cri.amount}
